DynamicLayoutGroup.attributes.add('mobilePortraitPadding', { type: 'vec4', default: [ -1, -1, -1, -1 ], title: 'Padding' });
DynamicLayoutGroup.attributes.add('mobilePortraitSpacing', { type: 'vec2', default: [ -1, -1 ], title: 'Spacing' });

