var animationStates = Object.freeze({ IDLE: 0, ANIMATE: 1, LEAVE: 2, ADDTOCOLLECTION: 3, FUSE: 4, READYFORFUSE: 5 });

CollectibleUnlockInterface.attributes.add('spineObject', { type: 'entity' });
