BuyItem.TYPES_ENUM = Object.freeze([
    { BUNDLE: 'bundle' },
    { VIRTUAL_CURRENCY_PACKAGE: 'virtualCurrency' },
]);

BuyItem.attributes.add('key', { type: 'string' });
